let WebsitePV=0;let WebsiteUV=0;function updatePageViews(){const pvContainer=document.getElementById("website_pv_container");const uvContainer=document.getElementById("website_uv_container");const pvSpan=document.getElementById("website_pv");const uvSpan=document.getElementById("website_uv");const website=window.location.hostname;const data={website:website};fetch("https://api1.bmft.tech/dc3/api_platform/add_view",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(data)}).then(response=>response.json()).then(data=>{WebsitePV=data.data.pv;WebsiteUV=data.data.uv;if(pvSpan!==null){pvSpan.textContent=WebsitePV}if(uvSpan!==null){uvSpan.textContent=WebsiteUV}if(pvContainer!==null){pvContainer.style.display="block"}if(uvContainer!==null){uvContainer.style.display="block"}}).catch(error=>console.error(error))}window.addEventListener("load",updatePageViews);