let WebsitePV=0;let WebsiteUV=0;async function updatePageViews(){try{const pvContainer=document.getElementById("website_pv_container");const uvContainer=document.getElementById("website_uv_container");const pvSpan=document.getElementById("website_pv");const uvSpan=document.getElementById("website_uv");const website=window.location.hostname;const data={website:website};if(WebsitePV==0){const response=await fetch("https://api1.bmft.tech/dc3/api_platform/add_view",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(data)});const resp=await response.json();WebsitePV=resp.data.pv;WebsiteUV=resp.data.uv}if(pvSpan!==null){pvSpan.textContent=WebsitePV}if(uvSpan!==null){uvSpan.textContent=WebsiteUV}if(pvContainer!==null){pvContainer.style.display="block"}if(uvContainer!==null){uvContainer.style.display="block"}}catch(error){console.error(error)}}window.addEventListener("load",updatePageViews);window.addEventListener("focus",updatePageViews);window.addEventListener("resize",updatePageViews);window.addEventListener("scroll",updatePageViews);window.addEventListener("blur",updatePageViews);